DROP TABLE IF EXISTS users CASCADE;
DROP TABLE IF EXISTS categories CASCADE;
DROP TABLE IF EXISTS events CASCADE;
DROP TABLE IF EXISTS participants_request CASCADE;
DROP TABLE IF EXISTS compilations CASCADE;
DROP TABLE IF EXISTS compilations_events CASCADE;


CREATE TABLE IF NOT EXISTS USERS (id BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY NOT NULL,name VARCHAR(250) NOT NULL,email VARCHAR(254) NOT NULL UNIQUE);

CREATE TABLE IF NOT EXISTS categories (id BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY NOT NULL, name VARCHAR(50) NOT NULL UNIQUE);

CREATE TABLE IF NOT EXISTS events (id BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY NOT NULL,title VARCHAR(120) NOT NULL,annotation VARCHAR(2000) NOT NULL,description VARCHAR(7000) NOT NULL,category_id BIGINT NOT NULL,initiator_id BIGINT NOT NULL,createdOn TIMESTAMP WITHOUT TIME ZONE NOT NULL,eventDate TIMESTAMP WITHOUT TIME ZONE NOT NULL,publishedOn TIMESTAMP WITHOUT TIME ZONE NOT NULL,loc_lat decimal NOT NULL,loc_lon decimal NOT NULL,paid BOOLEAN NOT NULL,participantLimit INT NOT NULL,requestModeration BOOLEAN NOT NULL,state VARCHAR(50) NOT NULL,FOREIGN KEY (category_id) REFERENCES categories(id) ON DELETE CASCADE,FOREIGN KEY (initiator_id) REFERENCES users(id) ON DELETE CASCADE);

CREATE TABLE IF NOT EXISTS participants_request (id BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY NOT NULL, requester_id BIGINT NOT NULL, event_id BIGINT NOT NULL, created TIMESTAMP WITHOUT TIME ZONE NOT NULL, status VARCHAR(50) NOT NULL, FOREIGN KEY (event_id) REFERENCES events(id) ON DELETE CASCADE,	FOREIGN KEY (requester_id) REFERENCES users(id) ON DELETE CASCADE);

CREATE TABLE IF NOT EXISTS compilations (id BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY NOT NULL, title VARCHAR(50) NOT NULL, pinned BOOLEAN NOT NULL);

CREATE TABLE IF NOT EXISTS compilations_events (compilation_id BIGINT NOT NULL,event_id BIGINT NOT NULL,FOREIGN KEY (event_id) REFERENCES events(id) ON DELETE CASCADE,FOREIGN KEY (compilation_id) REFERENCES compilations(id) ON DELETE CASCADE);